import React from 'react'
import { connect } from 'react-redux'


import s from './Body.module.css'
import { addMessage } from './chatReducer'
import MessageFormRedux from './Message/messageForm'

import Users from './Users/Users'
import WindowChat from './windowChat/windowChat'

class Body extends React.Component{


    render(){

    return(
        <div className={s.body}>
            <div className={s.chat}>
                <WindowChat messageName={this.props.message} />
            </div>
            <div className={s.people}>
                <Users />
            </div>
            <div className={s.mess}>
               <MessageFormRedux />
                <input className={s.input}></input>
                <button className={s.button} onClick={this.props.addMessage}>SEND</button>
            </div>
        </div>

    )}
}





const mapStateToProps=(state)=>({

    message: state.MessageReducer.message
})


export const BodyContainer = connect(mapStateToProps, addMessage)(Body)
