let ADD_USERS = 'ADD_USERS'


const initialState = {
    users:[
    {id: 1 , login: 'Ivan', pass: '123'},
    {id: 2 , login: 'Dima', pass: '123'}
    ]
}


export const reducerHeader = (state = initialState, action) => {
    console.log(state.users.map())
    switch (action.type){
        case ADD_USERS: {

            if(action.login===state.users.map(login=>login.login)){
                return state
            }

            let newUsers={
                id: 3,
                login: action.login,
                pass: action.pass

            }

            return {...state, users: [...state.users, newUsers]}
        }

        default:
            return state
    }
}

export const addUsers = (login , pass) =>({type: ADD_USERS, login, pass })
