import React from 'react'
import s from './windowChat.module.css'
import $ from 'jquery'
import {findDOMNode} from 'react-dom'


const WindowChat=(props)=>{

    const handleScroll=()=>{
        const el = findDOMNode(this.refs.toggle)
        $(el).scrollTop
    }



    return(

        <div id='chat' className={s.chat} onCh>
            {props.messageName.map(u=><div key={u.id}><span>{u.name + ': '}</span><span>{u.message}</span></div>)}
        </div>

    )
}


export default WindowChat
