import React from 'react'
import s from './windowChat.module.css'



class WindowChat extends React.Component{

    componentDidUpdate() {
        this.refs.elem.scrollTop = this.props.scrollTop;
    }
    onScroll() {
        this.props.handleScroll( this.refs.elem.scrollTop );
    }


    render(){

    return(

        <div onSc className={s.chat} onClick={this.handleScroll}>
            {this.props.messageName.map(u=><div key={u.id}><span>{u.name + ': '}</span><span>{u.message}</span></div>)}
        </div>

    )}
}


export default WindowChat
