let ADD_USERS = 'ADD_USERS'


const initialState = {
    users:[
    {id: 1 , login: 'Ivan', pass: '123'},
    {id: 2 , login: 'Dima', pass: '123'}
    ]
}


export const reducerHeader = (state = initialState, action) => {
    switch (action.type){
        case ADD_USERS: {
            let newUsers={
                id: 3,
                login: action.users.login,
                pass: action.users.pass

            }

            return {...state, users: [...state.users, newUsers]}
        }

        default:
            return state
    }
}

export const addUsers = (users) =>({type: ADD_USERS, users })
